rule "Filebeat - DHCP Server - Grok Pattern"
when
  has_field("message") AND contains(to_string($message.tags),"dhcpserver")
then
  let msg = to_string($message.message);
  let dhcp_server_log = grok(pattern: "^%{INT:dhcpv4_id},(?<dhcpv4_logtime>(%{MONTHNUM}\\/%{MONTHDAY}\\/%{YEAR},%{HOUR}:%{MINUTE}:%{SECOND})),%{GREEDYDATA:dhcpv4_option_message_type}?((,%{IP:dhcpv4_client_ip},%{DATA:dhcpv4_option.hostname},%{DATA:dhcpv4_client_mac},%{DATA:user_name})|(,,%{DATA:dhcpv4_option_hostname},,)|(,,,,)),%{WORD:dhcpv4_transaction_id},%{WORD:dhcpv4_op_code},,?,,%{DATA:dhcpv4_option_class_identifier},%{DATA:dhcpv4_option_vendor_identifying_options}?((,,,,)|,%{WORD:dhcpv4_option_user_identifying_options},%{WORD:dhcpv4_option_relay_agent_information},,)%{WORD:dhcpv4_option_message}", value: to_string(msg), only_named_captures: true);
  set_fields(dhcp_server_log);
end

rule "Filebeat - DNS Server - Grok Pattern"
when
  has_field("tags") AND contains(to_string($message.tags),"dnsserver")
then
  let msg = to_string($message.message);
  let dns_server_log = grok(pattern: "(?<datetimestamp>(%{MONTHNUM}/%{MONTHDAY}/%{YEAR} %{HOUR}:%{MINUTE}:%{SECOND}\\s+%{WORD:thread_id}))\\s+%{WORD:context}\\s+\\s+%{WORD:packet_id}\\s+%{WORD:network_transport}\\s+(?<network_direction>Snd|Rcv)\\s+%{IP:resolved_ip}\\s+%{WORD:dns_id}\\s+((?<dns_type>R)\\s+)?%{WORD:dns_op_code}\\s+\\[\\d+\\s+((?<dns_header_flags>\\S.+)\\s+)?%{WORD:dns_response_code}\\]\\s+%{WORD:dns_question_type}\\s+\\(\\d+\\)%{GREEDYDATA:dns_question_name}\\(0\\)", value: to_string(msg), only_named_captures: true);
  set_fields(dns_server_log);
end

rule "Filebeat - DHCP Server - Prefix Extractor"

when
  has_field("dhcpv4_client_mac")
then
   let extract_prefix = regex(
       pattern: "^([\\dA-Za-z]{6})",
       value: to_string($message.dhcpv4_client_mac)
       );
   set_field("dhcpv4_client_mac_prefix", to_string(extract_prefix["0"]));

end

rule "Winlogbeat - W-Defender - Message Replace"

when
