rule "Winlogbeat - Registry - Code and CodeDescription Lookup"

when
  has_field("tags") AND regex("windowsregistry", to_string($message.tags)).matches == true
  then
   let update_source = lookup_value("registry_code_to_description", to_string($message.winlog_event_data_OperationType));
   set_field("winlog_event_data_OperationTypeDescription", update_source);

   let update_source2 = lookup_value("registry_code_to_description", to_string($message.winlog_event_data_OldValueType));
   set_field("winlog_event_data_OldValueTypeDescription", update_source2);

   let update_source3 = lookup_value("registry_code_to_description", to_string($message.winlog_event_data_NewValueType));
   set_field("winlog_event_data_NewValueTypeDescription", update_source3);

   let msg = to_string($message.winlog_event_data_ObjectName);
   let registry_01 = grok(pattern: "\\\\REGISTRY\\\\%{WORD:winlog_event_data_Registry_Type}\\\\%{WORD:winlog_event_data_Registry_Hive_Type}\\\\", value: to_string(msg), only_named_captures: true);
   set_fields(registry_01);

end

rule "Filebeat - Windows Firewall - Grok Pattern"
when
  has_field("beats_type") AND regex("filebeat-windows-firewall-domain|filebeat-windows-firewall-private|filebeat-windows-firewall-public", to_string($message.tags)).matches == true
then
  let msg = to_string($message.message);
  let filebeat_windows_firewall_log = grok(pattern: "%{TIMESTAMP_ISO8601} %{WORD:action} %{WORD:protocol} %{IP:source_ip} %{IP:destination_ip} %{BASE10NUM:SrcPort} %{BASE10NUM:DstPort} %{DATA:Size} %{DATA:TcpFlags} %{DATA:TcpSync} %{DATA:TcpAck} %{DATA:TcpWin} %{DATA:IcmpType} %{DATA:IcmpCode} %{DATA:Info} %{DATA:Path} %{GREEDYDATA:Pid}", value: to_string(msg), only_named_captures: true);
  set_fields(filebeat_windows_firewall_log);
end

rule "Winlogbeat - W-Defender - Path Extractor"

when
  has_field("winlog_event_data_Path")
then
   let extract_containerfile = regex(
       pattern: "^.*containerfile:_\"?(.+?)\"? .*$",
       value: to_string($message.winlog_event_data_Path)
       );
   set_field("containerfile", to_string(extract_containerfile["0"]));

   let extract_file = regex(
       pattern: "^.*file:_\"?(.+?)\"? .*$",
       value: to_string($message.winlog_event_data_Path)
       );
   set_field("file", to_string(extract_file["0"]));

   let extract_webfile = regex(
       pattern: "^.*webfile.*\\|(.*)\\|.*$",
       value: to_string($message.winlog_event_data_Path)
       );
   set_field("webfile", to_string(extract_webfile["0"]));




end

rule "Winlogbeat - Firewall - Application Name Extractor"

when
  has_field("winlog_event_data_Application") 
then
   let extract_app = regex(
       pattern: "([^\\\\]+$)",
       value: to_string($message.winlog_event_data_Application)
       );
   set_field("firewall_Application_name", to_string(extract_app["0"]));

end

